import React, { useEffect, useState } from "react";
import EventsList from "../components/EventsList";

const Events = () => {
  const [error, setError] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [eventData, setEvents] = useState([]);
  useEffect(() => {
    setIsLoading(true);
    const fetchEvents = async () => {
      const response = await fetch("http://localhost:8080/events");
      if (!response.ok) {
        throw new Error("Error fetch event data");
      }
      const fetchedEvent = await response.json();
      setEvents(fetchedEvent);
      console.log(fetchedEvent.events);
    };
    try {
      fetchEvents();
      setIsLoading(false);
    } catch (error) {
      console.log(error);
      setError(error);
    }
  }, []);
  return (
    <React.Fragment>
      <div style={{ textAlign: "center" }}>
        {isLoading && <p> ...Loading </p>}
        {error && <p> {error} </p>}
        {!isLoading && eventData && <EventsList events={eventData} />}
      </div>
    </React.Fragment>
  );
};

export default Events;
