import React from "react";
import EventForm from "../components/EventForm";
import { json, redirect } from "react-router-dom";

const NewEvent = () => {
  return (
    <React.Fragment>
      <EventForm />
    </React.Fragment>
  );
};

export default NewEvent;

export const action = ({ request, params }) => {
  const data = request.get.formData();
  const enterValue = {
    title: data.title,
    image: data.image,
    date: data.date,
    description: data.description,
  };
  const response = fetch("https://localhost:8080/events", {
    method: "POST",
    body: enterValue,
  });
  if (!response.ok) {
    throw json(
      { message: "unable to add event to the database" },
      { status: 500 }
    );
  }
  return redirect("/events");
};
